queries.query:
SELECT A.* FROM (SELECT B.*, ROWNUM B_ROWNUM FROM (

SELECT *
FROM D_MM${localChannelId}
WHERE ID = ? AND STATUS = ? AND SERVER_ID = ?
ORDER BY MESSAGE_ID

) B WHERE ROWNUM  <=  (${offset} + ${limit})) A WHERE B_ROWNUM > ${offset}

SELECT A.* FROM (SELECT B.*, ROWNUM B_ROWNUM FROM (

SELECT *
FROM D_MM${localChannelId}
WHERE ID = ? AND STATUS = ?
ORDER BY MESSAGE_ID

) B WHERE ROWNUM  <=  (${offset} + ${limit})) A WHERE B_ROWNUM > ${offset}

SELECT A.* FROM (SELECT B.*, ROWNUM B_ROWNUM FROM (

SELECT *
FROM D_MM${localChannelId}
WHERE ID = ? AND STATUS = ? AND SERVER_ID = ?
AND MESSAGE_ID  >=  ? AND MESSAGE_ID  <=  ?
ORDER BY MESSAGE_ID

) B WHERE ROWNUM  <=  (${offset} + ${limit})) A WHERE B_ROWNUM > ${offset}

SELECT A.* FROM (SELECT B.*, ROWNUM B_ROWNUM FROM (

SELECT *
FROM D_MM${localChannelId}
WHERE ID = ? AND STATUS = ?
AND MESSAGE_ID  >=  ? AND MESSAGE_ID  <=  ?
ORDER BY MESSAGE_ID

) B WHERE ROWNUM  <=  (${offset} + ${limit})) A WHERE B_ROWNUM > ${offset}

SELECT A.* FROM (
SELECT M.* FROM D_M${localChannelId} M JOIN D_MM${localChannelId} MM ON MM.MESSAGE_ID = M.ID AND MM.ID = 0 WHERE M.ID >= ? AND M.PROCESSED = 0 AND MM.SERVER_ID = ? AND MM.STATUS != 'R' ORDER BY M.ID
) A WHERE ROWNUM <= ${limit}

SELECT A.* FROM  (

SELECT DISTINCT MESSAGE_ID
FROM D_MM${localChannelId}
WHERE MESSAGE_ID  >=  ? AND ID > 0 AND STATUS = 'P' AND SERVER_ID = ?
ORDER BY MESSAGE_ID

) A WHERE ROWNUM  <=  ${limit}
