queries.query:
SELECT a.* FROM (SELECT b.*, rownum b_rownum FROM (
SELECT mm.*, m.server_id FROM d_mm${localChannelId} mm JOIN d_m${localChannelId} m ON m.id = mm.message_id WHERE mm.id = ? AND mm.status = ? ORDER BY m.id
) b WHERE rownum <= (${offset} + ${limit})) a WHERE b_rownum > ${offset}

SELECT a.* FROM (SELECT b.*, rownum b_rownum FROM (
SELECT mm.*, m.server_id FROM d_mm${localChannelId} mm JOIN d_m${localChannelId} m ON m.id = mm.message_id WHERE mm.id = ? AND mm.status = ? AND m.id >= ? AND m.id <= ? ORDER BY m.id
) b WHERE rownum <= (${offset} + ${limit})) a WHERE b_rownum > ${offset}
