mapper.select:
SELECT message_id, metadata_id, content_type, content, data_type, is_encrypted
FROM d_mc${localChannelId}
WHERE message_id = #{messageId}
AND metadata_id = 0
AND content_type = 1

SELECT metadata_id, message_id, content_type, content, data_type, encrypted
FROM d_mc${localChannelId} WHERE message_id = #{messageId} AND metadata_id IN
#{metaDataId}

SELECT id, type from d_ma${localChannelId}
WHERE message_id = #{messageId}
GROUP BY id, type

