mapper.sql:
SELECT m.id FROM d_m${localChannelId} m
AND m.received_date < #{dateThreshold}
FETCH FIRST ${limit} ROWS ONLY

sql.if:
AND NOT EXISTS ( SELECT 1 FROM d_mm${localChannelId} mm WHERE mm.message_id = m.id AND mm.status IN
#{status}
)
