mapper.select:
		SELECT ID, USERNAME, FIRSTNAME, LASTNAME, ORGANIZATION, INDUSTRY, EMAIL, PHONENUMBER, LAST_LOGIN, GRACE_PERIOD_START, DESCRIPTION
		FROM PERSON
		
         	ID = #{id}
         	AND USERNAME = #{username}

mapper.delete:
DELETE FROM PERSON_PASSWORD
WHERE PERSON_ID = #{id}
AND PASSWORD_DATE  <=  #{pruneDate}

mapper.update:
UPDATE PERSON
SET GRACE_PERIOD_START = #{gracePeriodStart}
WHERE ID = #{id}

UPDATE PERSON
SET GRACE_PERIOD_START = NULL
WHERE ID = #{id}

UPDATE PERSON
SET LOGGED_IN = 1,
LAST_LOGIN = #{lastLogin}
WHERE ID = #{id}

UPDATE PERSON
SET LOGGED_IN = 0
WHERE ID = #{id}

mapper.select:
SELECT LOGGED_IN
FROM PERSON
WHERE ID = #{id}

SELECT NAME AS "key", VALUE AS "value"
FROM PERSON_PREFERENCES
WHERE PERSON_ID = #{value}

SELECT VALUE
FROM PERSON_PREFERENCES
WHERE PERSON_ID = #{person_id} AND NAME = #{name}

mapper.update:
UPDATE PERSON_PREFERENCES
SET VALUE = #{value}
WHERE PERSON_ID = #{person_id} AND NAME = #{name}

mapper.delete:
DELETE FROM PERSON_PREFERENCES
WHERE PERSON_ID = #{person_id}

	AND NAME = #{name}
