mapper.select:
SELECT MESSAGE_ID, METADATA_ID, CONTENT_TYPE, CONTENT, DATA_TYPE, IS_ENCRYPTED
FROM D_MC${localChannelId}
WHERE MESSAGE_ID = #{messageId}
AND METADATA_ID = 0
AND CONTENT_TYPE = 1

mapper.sql:
FROM D_M${localChannelId} M
JOIN D_MM${localChannelId} MM ON MM.MESSAGE_ID = M.ID


	LEFT JOIN D_MCM${localChannelId} MCM ON MCM.MESSAGE_ID = MM.MESSAGE_ID AND MCM.METADATA_ID = MM.ID




		LEFT JOIN D_MC${localChannelId} MC_${element.contentCode} ON MC_${element.contentCode}.MESSAGE_ID = M.ID AND MC_${element.contentCode}.IS_ENCRYPTED = 0




				AND MC_${element.contentCode}.METADATA_ID = 0 AND ((MM.ID = 0 AND MC_${element.contentCode}.CONTENT_TYPE = 1) OR (MM.ID > 0 AND MC_${element.contentCode}.CONTENT_TYPE = 4))


				AND MC_${element.contentCode}.METADATA_ID = MM.ID AND MC_${element.contentCode}.CONTENT_TYPE = #{element.contentCode}







		M.ID  >=  #{messageIdLower}


		AND M.ID  <=  #{messageIdUpper}


		AND M.ID  <=  #{maxMessageId}


		AND M.RECEIVED_DATE  >=  #{startDate}


		AND M.RECEIVED_DATE  <=  #{endDate}


		AND M.SERVER_ID LIKE '%' || #{serverId} || '%'


		AND M.ORIGINAL_ID  >=  #{originalIdLower}


		AND M.ORIGINAL_ID  <=  #{originalIdUpper}


		AND M.IMPORT_ID  >=  #{importIdLower}


		AND M.IMPORT_ID  <=  #{importIdUpper}


		AND (
		CAST(M.ID AS VARCHAR2(255 CHAR)) LIKE '%' || #{textSearch} || '%'
		OR MM.CONNECTOR_NAME LIKE '%' || #{textSearch} || '%'



				OR MC_${element.contentCode}.CONTENT LIKE '%' || #{textSearch} || '%'




			OR MCM."${column}" LIKE '%' || #{textSearch} || '%'

		)


		AND MM.STATUS IN

			#{status}



		AND MM.ID IN

			#{metaDataId}



		AND MM.ID NOT IN

			#{metaDataId}






				AND MC_${element.contentCode}.CONTENT LIKE '%' || #{searchString} || '%'





		AND MM.SEND_ATTEMPTS  >=  #{sendAttemptsLower}


		AND MM.SEND_ATTEMPTS  <=  #{sendAttemptsUpper}


		AND EXISTS (SELECT 1 FROM D_MA${localChannelId} MA WHERE MA.MESSAGE_ID = M.ID)


		AND MM.ERROR_CODE  >  0



			AND 



					UPPER(MCM."${element.columnName}")


					MCM."${element.columnName}"





					 =  


					 !=  


					 <  


					 <=  


					 >  


					 >=  


					LIKE




				'%' ||




					UPPER(#{element.value})


					#{element.value}




				|| '%'

mapper.select:
SELECT METADATA_ID, MESSAGE_ID, CONTENT_TYPE, CONTENT, DATA_TYPE, IS_ENCRYPTED
FROM D_MC${localChannelId} WHERE MESSAGE_ID = #{messageId} AND METADATA_ID IN
#{metaDataId}

SELECT ID, TYPE FROM D_MA${localChannelId}
WHERE MESSAGE_ID = #{messageId}
GROUP BY ID, TYPE

mapper.delete:
DELETE FROM D_M${localChannelId}
WHERE ID IN (
	SELECT M.ID
	FROM D_M${localChannelId} M


		JOIN D_MM${localChannelId} MM ON MM.MESSAGE_ID = M.ID AND MM.ID = 0 AND MM.RECEIVED_DATE  <  #{dateThreshold}






			AND ROWNUM  <=  ${limit}


)

DELETE FROM D_MC${localChannelId}
WHERE MESSAGE_ID IN (
	SELECT M.ID
	FROM D_M${localChannelId} M

		JOIN D_MM${localChannelId} MM ON MM.MESSAGE_ID = M.ID AND MM.ID = 0 AND MM.RECEIVED_DATE  <  #{dateThreshold}






			AND ROWNUM  <=  ${limit}


)

sql.if:
AND NOT EXISTS ( SELECT 1 FROM D_MM${localChannelId} MM WHERE MM.MESSAGE_ID = M.ID AND MM.STATUS IN
#{status}
)

mapper.select:
SELECT A.* FROM (SELECT B.*, ROWNUM B_ROWNUM FROM (
SELECT M.ID, M.SERVER_ID, M.RECEIVED_DATE, M.PROCESSED, M.ORIGINAL_ID, M.IMPORT_ID, M.IMPORT_CHANNEL_ID, MM.RECEIVED_DATE AS MM_RECEIVED_DATE
FROM D_M${localChannelId} M JOIN D_MM${localChannelId} MM ON MM.MESSAGE_ID = M.ID AND MM.ID = 0
LEFT JOIN D_MM${localChannelId} MM_SKIP ON MM_SKIP.MESSAGE_ID = M.ID AND MM_SKIP.STATUS IN
#{status}
WHERE MM.RECEIVED_DATE < #{dateThreshold}
AND M.PROCESSED = 1
AND MM_SKIP.ID IS NULL
ORDER BY M.ID
) B WHERE ROWNUM <= (${offset} + ${limit})) A WHERE B_ROWNUM > ${offset}
