mapper.select:
SELECT message.id, message.server_id, message.received_date, message.processed, message.import_id, message.import_channel_id, metadata_ids
FROM ( SELECT m.id, array_to_string(array_agg(mm.id::character varying), ',') AS metadata_ids
GROUP BY m.id
ORDER BY m.id DESC
OFFSET ${offset}
LIMIT ${limit}
) AS subquery INNER JOIN d_m${localChannelId} message ON subquery.id = message.id ORDER BY message.id DESC

SELECT message_id, metadata_id, content_type, content, data_type, is_encrypted
FROM d_mc${localChannelId}
WHERE message_id = #{messageId}
AND metadata_id = 0
AND content_type = 1

mapper.sql:
FROM d_m${localChannelId} m
JOIN d_mm${localChannelId} mm ON mm.message_id = m.id


	LEFT JOIN d_mcm${localChannelId} mcm ON mcm.message_id = mm.message_id AND mcm.metadata_id = mm.id




		LEFT JOIN d_mc${localChannelId} mc_${element.contentCode} ON mc_${element.contentCode}.message_id = m.id AND mc_${element.contentCode}.is_encrypted = FALSE




				AND mc_${element.contentCode}.metadata_id = 0 AND ((mm.id = 0 AND mc_${element.contentCode}.content_type = 1) OR (mm.id > 0 AND mc_${element.contentCode}.content_type = 4))


				AND mc_${element.contentCode}.metadata_id = mm.id AND mc_${element.contentCode}.content_type = #{element.contentCode}







		m.id  >=  #{messageIdLower}


		AND m.id  <=  #{messageIdUpper}


		AND m.id  <=  #{maxMessageId}


		AND m.received_date  >=  #{startDate}


		AND m.received_date  <=  #{endDate}


		AND m.server_id ILIKE '%' || #{serverId} || '%'


		AND m.import_id  >=  #{importIdLower}


		AND m.import_id  <=  #{importIdUpper}


		AND (
		m.id::varchar ILIKE '%' || #{quickSearch} || '%'
		OR mm.connector_name ILIKE '%' || #{quickSearch} || '%'



				OR mc_${element.contentCode}.content ILIKE '%' || #{quickSearch} || '%'




			OR mcm."${column}" ILIKE '%' || #{quickSearch} || '%'

		)


		AND mm.status IN

			#{status}



		AND mm.id IN

			#{metaDataId}



		AND mm.id NOT IN

			#{metaDataId}






				AND mc_${element.contentCode}.content ILIKE '%' || #{searchString} || '%'





		AND mm.send_attempts  >=  #{sendAttemptsLower}


		AND mm.send_attempts  <=  #{sendAttemptsUpper}


		AND EXISTS (SELECT 1 FROM d_ma${localChannelId} ma WHERE ma.message_id = m.id)



		AND mcm."${element.columnName}"



				 =  


				 !=  


				 <  


				 <=  


				 >  


				 >=  




						ILIKE


						LIKE






			'%' ||

			#{element.value}

			|| '%'

mapper.select:
SELECT *
FROM d_mc${localChannelId}
WHERE message_id = #{messageId}
AND metadata_id IN

	#{metaDataId}

SELECT id, type from d_ma${localChannelId}
WHERE message_id = #{messageId}
GROUP BY id, type

choose.when:
DELETE FROM d_m${localChannelId} m


	USING d_mm${localChannelId} mm






		AND mm.message_id = m.id AND mm.id = 0 AND mm.received_date  <  #{dateThreshold}

choose.otherwise:
DELETE FROM d_m${localChannelId}
WHERE id IN (
	SELECT m.id
	FROM d_m${localChannelId} m

		JOIN d_mm${localChannelId} mm0 ON mm0.message_id = m.id AND mm0.id = 0 AND mm0.received_date  <  #{dateThreshold}






	LIMIT #{limit}
)

mapper.delete:
DELETE FROM d_mc${localChannelId} mc



		USING d_m${localChannelId} m


			, d_mm${localChannelId} mm




			AND mc.message_id = m.id


				AND mm.message_id = mc.message_id AND mm.id = 0 AND mm.received_date  <  #{dateThreshold}




		WHERE message_id IN (
			SELECT mc.message_id
			FROM d_mc${localChannelId} mc
			JOIN d_m${localChannelId} m ON m.id = mc.message_id

				JOIN d_mm${localChannelId} mm0 ON mm0.message_id = m.id AND mm0.id = 0 AND mm0.received_date  <  #{dateThreshold}




			LIMIT #{limit}
		)

sql.if:
AND NOT EXISTS ( SELECT 1 FROM d_mm${localChannelId} mm WHERE mm.message_id = m.id AND mm.status IN
#{status}
)

mapper.select:
SELECT m.id, m.server_id, m.received_date, m.processed, m.import_id, m.import_channel_id, mm.received_date AS mm_received_date
FROM d_m${localChannelId} m JOIN d_mm${localChannelId} mm ON mm.message_id = m.id AND mm.id = 0
LEFT JOIN d_mm${localChannelId} mm_skip ON mm_skip.message_id = m.id AND mm_skip.status IN
#{status}
WHERE mm.received_date < #{dateThreshold}
AND m.processed = TRUE
AND mm_skip.id IS NULL
ORDER BY m.id
OFFSET #{offset} LIMIT #{limit}
