mapper.select:
		SELECT ID, USERNAME, FIRSTNAME, LASTNAME, ORGANIZATION, EMAIL, PHONENUMBER, LAST_LOGIN, GRACE_PERIOD_START, DESCRIPTION
		FROM PERSON

         AND ID = #{id}
         AND USERNAME = #{username}

mapper.delete:
DELETE FROM PERSON_PASSWORD
WHERE PERSON_ID = #{id}
AND PASSWORD_DATE  <=  '${pruneDate}'

mapper.update:
UPDATE PERSON
SET GRACE_PERIOD_START = CURRENT_TIMESTAMP
WHERE ID = #{value}

UPDATE PERSON
SET GRACE_PERIOD_START = NULL
WHERE ID = #{value}

UPDATE PERSON
SET LOGGED_IN = FALSE
WHERE ID = #{value}

mapper.select:
SELECT LOGGED_IN
FROM PERSON
WHERE ID = #{value}
